mapper.select:
select
  id, email, password, name, gender, photo, autograph, privacy_protection, praise_numb, thank_numb, question_numb, answer_numb, collect_numb, edit_numb, attention_numb, follower_numb, concern_topic_numb, personality_url, email_activate, forbidden, forbidden_time, role, create_time
from jijiedi.user


        and id = #{id}


        and email = #{email}


        and password = #{password}


        and name = #{name}


        and gender = #{gender}


        and photo = #{photo}


        and autograph = #{autograph}


        and privacy_protection = #{privacyProtection}


        and praise_numb = #{praiseNumb}


        and thank_numb = #{thankNumb}


        and question_numb = #{questionNumb}


        and answer_numb = #{answerNumb}


        and collect_numb = #{collectNumb}


        and edit_numb = #{editNumb}


        and attention_numb = #{attentionNumb}


        and follower_numb = #{followerNumb}


        and concern_topic_numb = #{concernTopicNumb}


        and personality_url = #{personalityUrl}


        and email_activate = #{emailActivate}


        and forbidden = #{forbidden}


        and forbidden_time = #{forbiddenTime}


        and role = #{role}


        and create_time = #{createTime}
